                $this->load->view('template/sidebar');
                $this->load->view('kmi/pelanggaran/input_pelanggaran', $data);
                $this->load->view('template/footer');
            }
        }
        else
            show_error('The Pelanggaran Santri you are trying to input does not exist.');
    }

