            $data['title'] = "Data Santri Yang Mendapat Hukuman Oleh $penghukum";
            $data['datafilter'] =$this->Pelanggaran_kmi_model->filter_by_penghukum($penghukum);
    
            $this->load->view('kmi/filter/print_laporan', $data);
            $this->load->view('template/footer', $data);
    
        }

        if ($nilaitanggal == 1) {
            $data['title'] = "Data Santri Yang Melanggar Dari $tanggalawal Sampai $tanggalakhir" ;
            $data['datafilter'] =$this->Pelanggaran_kmi_model->filter_by_tanggal($tanggalawal,$tanggalakhir);
    
            $this->load->view('kmi/filter/print_laporan', $data);
            $this->load->view('template/footer', $data);
    
        }

        if ($nilairayon == 1) {
            $data['title'] = "Data Santri Yang Melanggar Dari Rayon $rayon";
            $data['datafilter'] =$this->Pelanggaran_kmi_model->filterpelanggaran_by_rayon($rayon);
    
            $this->load->view('kmi/filter/print_laporan', $data);
            $this->load->view('template/footer', $data);
    
        }
    }

    /*
