            {   
                $nama_pelanggaran = $this->input->post('nama_pelanggaran');
                $kategori_pelanggaran = $this->input->post('kategori');

                $data = array(
                    'nama_pelanggaran' => $nama_pelanggaran,
                    'kategori' => $kategori_pelanggaran,);

                $this->Pelanggaran_kmi_model->update_kategori_pelanggaran($id_kategori,$data);
                $this->session->set_flashdata('berhasil', 'Anda berhasil mengubah data pelanggaran <strong>' . $nama_pelanggaran . '</strong>' . ' dengan kategori <strong>' . $kategori_pelanggaran . '</strong>' );

                redirect(base_url('kmi/pelanggaran/tambah_kategori_pelanggaran')) ;
            }
            else
            {
                
                $data['_view'] = 'kmi/pelanggaran/edit_kategori_pelanggaran';
                $this->load->view('template/header',$data);
                $this->load->view('template/sidebar',$data);
                $this->load->view('kmi/pelanggaran/edit_kategori_pelanggaran',$data);
                $this->load->view('template/footer',$data);
            }
        }
        else
            show_error('The Kategori Pelanggaran Santri you are trying to edit does not exist.');
    }

    /*
     * Function of hapus KATEGORI pelanggaran siswa
     */
    function hapus_kategori_pelanggaran($id_kategori)
    {
        // check if the pelanggaran exists before trying to edit it
        $data['kategori_pelanggaran'] = $this->Pelanggaran_kmi_model->get_kategori_pelanggaran($id_kategori);

        if(isset($data['kategori_pelanggaran']['id']))
        {
            $this->Pelanggaran_kmi_model->delete_kategori_pelanggaran($id_kategori);
            $this->session->set_flashdata('hapus', 'Anda berhasil menghapus data pelanggaran <strong>' .  $data['kategori_pelanggaran']['nama_pelanggaran'] . '</strong>' . ' dengan kategori <strong>' .  $data['kategori_pelanggaran']['kategori'] . '</strong>' );
            redirect(base_url('kmi/pelanggaran/tambah_kategori_pelanggaran')) ;
        } else
        show_error('The kategori pelanggaran you are trying to delete does not exist.');
    }
}
