        return $this->db->update('record_pelanggaran_pusdac',$params);
    }

    /*
     * function to delete pelanggaran santri
     */
    function delete_pelanggaran($id_pelanggaran)
    {
        $this->db->select('*, record_pelanggaran_pusdac.id as id_pelanggaran, siswa.id as id_siswa ');
        $this->db->from('record_pelanggaran_pusdac');
        $this->db->join('siswa','siswa.id = record_pelanggaran_pusdac.id_siswa');
        $this->db->where('record_pelanggaran_pusdac.id', $id_pelanggaran);
        return $this->db->delete('record_pelanggaran_pusdac',array('id'=>$id_pelanggaran));
    }

