    ///////////////////////////////////////////////////// POIN PELANGGARAN SANTRI ////////////////////////////////////////////////////////////////

    /*
     * function to tambah point pelanggaran siswa
     */
    function tambah_poin_pelanggaran($id_siswa, $datapoint, $poin_pelanggaran)
    {
        $this->db->where('id_siswa', $id_siswa);
        $q = $this->db->get('rekap_point_bahasa');
        $this->db->reset_query();
            
        if ( $q->num_rows() > 0 ) 
        {
            $this->db->query("UPDATE rekap_point_bahasa SET poin_santri = poin_santri + $poin_pelanggaran WHERE id_siswa = $id_siswa");
        } else {
            $this->db->set('id_siswa', $id_siswa)->insert('rekap_point_bahasa', $datapoint);
        }
    }

    /*
     * function to tambah point pelanggaran siswa massal
     */
    function tambah_poin_pelanggaran_massal($id_siswa, $datapoint, $poin_pelanggaran)
    {
        $this->db->where_in('id_siswa', $id_siswa);
        $q = $this->db->get('rekap_point_bahasa');
        $this->db->reset_query();
            
        if ( $q->num_rows() > 0 ) 
        {
            $this->db->query("UPDATE rekap_point_bahasa SET poin_santri = poin_santri + $poin_pelanggaran WHERE id_siswa = $id_siswa");
        } else {
            $this->db->set('id_siswa', $id_siswa)->insert('rekap_point_bahasa', $datapoint);
        }
    }

    /*
     * function to delete point pelanggaran siswa 20 poin
     */
    function delete_point_pelanggaran_20($id_siswa)
    {
        $this->db->query("UPDATE rekap_point_bahasa SET poin_santri = poin_santri - 20 WHERE id_siswa = $id_siswa");
        $this->db->query("DELETE FROM rekap_point_bahasa WHERE id_siswa = $id_siswa AND poin_santri = 0 ");
    }

    /*
     * function to delete point pelanggaran siswa 25 poin
     */
    function delete_point_pelanggaran_25($id_siswa)
    {
        $this->db->query("UPDATE rekap_point_bahasa SET poin_santri = poin_santri - 25 WHERE id_siswa = $id_siswa");
        $this->db->query("DELETE FROM rekap_point_bahasa WHERE id_siswa = $id_siswa AND poin_santri = 0 ");
    }

    /*
