    /*
     * function to filter by tanggal
     */
    function filter_by_tanggal($tanggalawal,$tanggalakhir)
    {
        $this->db->select('*');
        $this->db->from('record_pelanggaran_pusdac');
        $this->db->join('siswa','siswa.id = record_pelanggaran_pusdac.id_siswa');
        $this->db->join('tb_staf_bahasa','tb_staf_bahasa.id = record_pelanggaran_pusdac.id_penghukum');
        $this->db->where("(create_at) BETWEEN '$tanggalawal' AND '$tanggalakhir'");
        $this->db->order_by('create_at', 'desc');
        $this->db->order_by('siswa.id', 'asc');
        return $this->db->get()->result();
    }

    /*
