    /*
     * function to menghitung data bicara bahasa indo
     */
    function filter_by_tanggal_bicaraindo($tanggalawal,$tanggalakhir)
    {
        $query = $this->db->query("SELECT * FROM record_pelanggaran_pusdac WHERE create_at BETWEEN '$tanggalawal' AND '$tanggalakhir' HAVING nama_pelanggaran = 'Berbicara Bahasa Indonesia'");
