    function count_sakit($tanggalawal,$tanggalakhir)
    {
        $query = $this->db->query("SELECT * FROM record_perizinan_kmi WHERE create_at BETWEEN '$tanggalawal' AND '$tanggalakhir' HAVING nama_perizinan = 'Sakit Di BKS'");
        return $query->num_rows();
