class Perizinan_pengasuhan_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * function to tambah perizinan siswa
     */
    function tambah_perizinan($data){
        $this->db->insert('record_perizinan_pengasuhan', $data);
    }

     /*
     * function to update perizinan
     */
    function update_perizinan($id_perizinan,$params)
    {
        $this->db->where('id',$id_perizinan);
        return $this->db->update('record_perizinan_pengasuhan',$params);
    }

    /*
     * function to delete perizinan siswa
     */
    function delete_perizinan($id_perizinan)
    {
        $this->db->select('*, record_perizinan_pengasuhan.id as id_perizinan, siswa.id as id_siswa ');
        $this->db->from('record_perizinan_pengasuhan');
        $this->db->join('siswa','siswa.id = record_perizinan_pengasuhan.id_siswa');
        $this->db->join('tb_tujuan_perizinan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->where('record_perizinan_pengasuhan.id', $id_perizinan);
        return $this->db->delete('record_perizinan_pengasuhan',array('id'=>$id_perizinan));
    }

    /*
     * Get perizinan by id perizinan
     */
    function get_perizinan($id)
    {
        $this->db->select('*, record_perizinan_pengasuhan.id as id_perizinan, siswa.id as id_siswa ');
        $this->db->from('record_perizinan_pengasuhan');
        $this->db->join('siswa','siswa.id = record_perizinan_pengasuhan.id_siswa');
        $this->db->join('tb_penghukum_pengasuhan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->join('tb_tujuan_perizinan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->where('record_perizinan_pengasuhan.id', $id);
        $this->db->order_by('nama_lengkap', 'desc');
        return $this->db->get()->row_array();

        // return $this->db->get_where('record_perizinan_pengasuhan',array('id'=>$id))->row_array();
    }
    
    /*
     * function to view detail perizinan siswa
     */
    function filter_by_id_siswa($id)
    {
        $this->db->select('*, record_perizinan_pengasuhan.id as id_perizinan, siswa.id as id_siswa, ');
        $this->db->from('record_perizinan_pengasuhan');
        $this->db->join('siswa','siswa.id = record_perizinan_pengasuhan.id_siswa');
        $this->db->join('tb_penghukum_pengasuhan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->where('id_siswa', $id);
        $this->db->order_by('nama_lengkap', 'desc');
        return $this->db->get()->result();
    }

    /*
     * function to get tabel newest perizinan siswa
     */
    function get_tabel_perizinan_siswa()
    {
        $this->db->select('*, record_perizinan_pengasuhan.id as id_perizinan, siswa.id as id_siswa ');
        $this->db->from('record_perizinan_pengasuhan');
        $this->db->join('siswa','siswa.id = record_perizinan_pengasuhan.id_siswa');
        $this->db->join('tb_penghukum_pengasuhan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->order_by('create_at', 'desc');
        $this->db->order_by('siswa.id', 'asc');
        return $this->db->get()->result_array();
    }

