    function filter_by_perizinan($nama_perizinan)
    {
        $this->db->select('*,');
        $this->db->from('record_perizinan_pengasuhan');
        $this->db->join('siswa','siswa.id = record_perizinan_pengasuhan.id_siswa');
        $this->db->join('tb_penghukum_pengasuhan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->where('nama_perizinan', $nama_perizinan);
        $this->db->order_by('create_at', 'desc');
        $this->db->order_by('siswa.id', 'asc');
        return $this->db->get()->result();
    }


    function filter_by_pengizin($pengizin)
    {
        $this->db->select('*, tb_penghukum_pengasuhan.nama_penghukum as nama_pengizin,');
        $this->db->from('record_perizinan_pengasuhan');
        $this->db->join('siswa','siswa.id = record_perizinan_pengasuhan.id_siswa');
        $this->db->join('tb_penghukum_pengasuhan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->join('tb_tujuan_perizinan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->where('tb_penghukum_pengasuhan.nama_penghukum', $pengizin);
        $this->db->order_by('create_at', 'desc');
        $this->db->order_by('siswa.id', 'asc');
        // $this->db->where('rayon', "Syanggit Lt. 1");
        return $this->db->get()->result();
    }

    function filter_by_tanggal($tanggalawal,$tanggalakhir)
    {
        $this->db->select('*,');
        $this->db->from('record_perizinan_pengasuhan');
        $this->db->join('siswa','siswa.id = record_perizinan_pengasuhan.id_siswa');
        $this->db->join('tb_penghukum_pengasuhan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->join('tb_tujuan_perizinan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->where("(create_at) BETWEEN '$tanggalawal' AND '$tanggalakhir'");
        $this->db->order_by('create_at', 'desc');
        $this->db->order_by('siswa.id', 'asc');
        return $this->db->get()->result();
    }

    function filterperizinan_by_rayon($rayon)
    {
        $this->db->select('*,');
        $this->db->from('record_perizinan_pengasuhan');
        $this->db->join('siswa','siswa.id = record_perizinan_pengasuhan.id_siswa');
        $this->db->join('tb_penghukum_pengasuhan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->join('tb_tujuan_perizinan','tb_penghukum_pengasuhan.id = record_perizinan_pengasuhan.id_pengizin');
        $this->db->where('rayon', "$rayon");
        $this->db->order_by('create_at', 'desc');
        $this->db->order_by('siswa.id', 'asc');
        return $this->db->get()->result();
    }
    
