    /*
     * function to menghitung data kabur
     */
    function filter_by_tanggal_sakit($tanggalawal,$tanggalakhir)
    {
        $query = $this->db->query("SELECT * FROM record_perizinan_pengasuhan WHERE create_at BETWEEN '$tanggalawal' AND '$tanggalakhir' HAVING nama_perizinan = 'Perizinan Sakit'");
        return $query->num_rows();
    }

    function filter_by_tanggal_pulang_sementara($tanggalawal,$tanggalakhir)
    {
        $query = $this->db->query("SELECT * FROM record_perizinan_pengasuhan WHERE create_at BETWEEN '$tanggalawal' AND '$tanggalakhir' HAVING nama_perizinan = 'Perizinan Pulang Sementara'");
        return $query->num_rows();
    }

   
